DROP TABLE VIDEOS CASCADE CONSTRAINTS;

CREATE TABLE VIDEOS (
    ID NUMBER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    TITLE VARCHAR2(200) NOT NULL,
    CATEGORY VARCHAR2(100),
    GENRE VARCHAR2(100),
    LANGUAGE VARCHAR2(20),
    PRODUCTION_COMPANY VARCHAR2(200),
    DESCRIPTION VARCHAR2(2000),
    MATURITY_RATING VARCHAR2(20),
    RELEASE_YEAR NUMBER(4),
    DIRECTOR VARCHAR2(200),
    MAIN_CAST VARCHAR2(1000),
    IMDB_RATING NUMBER(3,1),
    THUMBNAIL_URL VARCHAR2(500),
    INTRO_VIDEO_URL VARCHAR2(500)
);

